			expr.pop();
			second = expr.top();
			expr.pop();
			if (current == '*')
				expr.push('('+first + '*'+second+')');
			else if (current == '/')
				expr.push('('+first +'/'+ second + ')');
			else if (current == '+')
				expr.push('(' + first + '+' + second + ')');
			else
				expr.push('(' + first + '-' + second + ')');
