	for (int p = 1; p < N; ++p)
	{
		Comparable tmp = std::move(array[p]);
		int j = p - 1;
		for (; j >= 0 && tmp < array[j]; --j)
		{
			array[j + 1] = std::move(array[j]); // j move right, p move left
		}
		array[j + 1] = std::move(tmp);
	}
};
